USE admitgenius_db; CREATE TABLE IF NOT EXISTS recommendation_items (id BIGINT AUTO_INCREMENT PRIMARY KEY, recommendation_id BIGINT, school_id BIGINT, program_id BIGINT, match_score FLOAT, `rank` INT, match_reason TEXT, feedback TEXT, is_applied BOOLEAN DEFAULT FALSE, FOREIGN KEY (recommendation_id) REFERENCES recommendations(id), FOREIGN KEY (school_id) REFERENCES schools(id), FOREIGN KEY (program_id) REFERENCES school_programs(id));
